  <div>
    <a-card :title="$t('users--index.self-block.title')" class="da-table-block dac-self-block">
      <a-row class="da-table-block-toolbar">
        <!-- Table Collection Operations -->
        <a-col :span="12">
          <a-space>
            <a-button
              type="primary"
              @click="goto({ name: 'UsersNew' })"
            >
              <template #icon>
                <icon-plus />
              </template>
              {{ $t('table.actions.new') }}
            </a-button>
          </a-space>
        </a-col>

        <!-- Table Search/Refresh/ColumnsSetting -->
        <a-col class="da-table-block-toolbar-r" :span="12">
          <a-space>
            <a-tag
              v-for="(value, name) in tableSearchCondition"
              :key="name"
              :visible="!!value"
              color="arcoblue"
              size="small"
              closable
              @close="handleSearchConditionClose(name as string)"
            >
              {{ t(searchMetadata[name]['i18nKey']) }}
              "{{ searchMetadata[name].optionals?.[value] ? t(searchMetadata[name].optionals[value]['i18nKey']) : value }}"
            </a-tag>
            <div
              v-show="tableSearchConditionCount"
              style="cursor: pointer"
              @click="handleSearchConditionClose('')"
            >
              <a-tag color="orangered" size="small">
                {{ t('table.actions.clearSearch') }}
              </a-tag>
            </div>
          </a-space>
          <a-tooltip :content="$t('table.actions.search')">
            <div class="action-btn" @click="handleSearchModalVisible">
              <a-badge
                color="arcoblue"
                dot
                :dot-style="{ width: '8px', height: '8px' }"
                :count="tableSearchConditionCount"
              >
                <icon-find-replace size="18"/>
              </a-badge>
            </div>
          </a-tooltip>
          <a-tooltip :content="$t('table.actions.refresh')">
            <div class="action-btn" @click="onTableRefresh">
              <icon-refresh size="18"/>
            </div>
          </a-tooltip>
          <a-tooltip :content="$t('table.actions.columnSetting')">
            <a-popover trigger="click" position="bl">
              <div class="action-btn"><icon-settings size="18" /></div>
              <template #content>
                <div>
                  <div v-for="(item, index) in tableColumnsWithShow" :key="item.dataIndex">
                    <div>
                      <a-checkbox
                        v-model="item.show"
                        @change="handleTableColumnsShowChange($event, item as TableColumnData, index)"
                      >
                        {{ item.title }}
                      </a-checkbox>
                    </div>
                  </div>
                </div>
              </template>
            </a-popover>
          </a-tooltip>
        </a-col>
