      <!-- Table -->
      <a-table
        row-key="id"
        :loading="loading"
        :pagination="tablePagination"
        :columns="(tableColumnsShow as TableColumnData[])"
        :data="tableData"
        @page-change="onTablePageChange"
        @sorter-change="onTableSorterChange"
      >
        <template #id="{ record, column }">
          <DullData
            :data="record[column.dataIndex]"
            :meta="modelMetadata[column.dataIndex]"
          />
        </template>
        <template #name="{ record, column }">
          <DullData
            :data="record[column.dataIndex]"
            :meta="modelMetadata[column.dataIndex]"
          />
        </template>
        <template #username="{ record, column }">
          <DullData
            :data="record[column.dataIndex]"
            :meta="modelMetadata[column.dataIndex]"
          />
        </template>
        <template #avatar="{ record, column }">
          <DullData
            :data="record[column.dataIndex]"
            :meta="modelMetadata[column.dataIndex]"
          />
        </template>
        <template #email="{ record, column }">
          <DullData
            :data="record[column.dataIndex]"
            :meta="modelMetadata[column.dataIndex]"
          />
        </template>
        <template #phone="{ record, column }">
          <DullData
            :data="record[column.dataIndex]"
            :meta="modelMetadata[column.dataIndex]"
          />
        </template>
        <template #website="{ record, column }">
          <DullData
            :data="record[column.dataIndex]"
            :meta="modelMetadata[column.dataIndex]"
          />
        </template>
        <!-- Table Model Operations -->
        <template #tableOperationsColumn="{ record, column }">
          <a-space>
            <a-button
              type="outline"
              status="success"
              size="mini"
              @click="goto({ name: 'UsersShow', params: { id: record.id } })"
            >
              <icon-eye />
            </a-button>
            <a-button
              type="outline"
              status="warning"
              size="mini"
              @click="goto({ name: 'UsersEdit', params: { id: record.id } })"
            >
              <icon-edit />
            </a-button>
            <a-button
              type="outline"
              status="danger"
              size="mini"
              @click="goto({ name: 'UsersDelete', params: { id: record.id } })"
            >
              <icon-delete />
            </a-button>
          </a-space>
        </template>
      </a-table>
    </a-card>

    <!-- Table Search -->
    <a-modal
      v-model:visible="searchModalVisible"
      class="da-form-dialog"
      :ok-text="$t('table.actions.search')"
      :on-before-ok="handleSearchModalBeforeOk"
      @cancel="handleSearchModalCancel"
    >
      <template #title>
        {{ $t('users--index.self-block.title') }}
        -
        {{ $t('table.actions.search') }}
      </template>
      <a-form :model="tableSearchForm" :auto-label-width="true">
        <DullFormItem
          v-model="tableSearchForm.id_eq"
          :meta="searchMetadata.id_eq"
        />
        <DullFormItem
          v-model="tableSearchForm.name_cont"
          :meta="searchMetadata.name_cont"
        />
      </a-form>
    </a-modal>

    <!-- Table Model Operations UI indicator -->
    <div v-show="false" ref="tableOperationsColumnRenderableRef">
      <span />
      <span />
      <span />
    </div>
  </div>
</template>

<script lang="ts" setup>
  import { computed, reactive, ref, watch, onMounted } from 'vue';
  import { useRouter, useRoute, RouteLocationRaw } from 'vue-router';
  import { useI18n } from 'vue-i18n';
  import { cloneDeep, omitBy, isEmpty } from 'lodash';
  import type { TableColumnData } from '@arco-design/web-vue/es/table/interface';
  import { Model, ListRequest, list } from '@/api/modules/users/index/self-block';
  import { useLoading, useTabbableViewBlock } from '@/hooks';

  // types
  type Column = TableColumnData & { show?: boolean, renderable?: boolean, hidden?: boolean };
  type Search = Record<string, any>;
  type Sorter = Record<string, any>;
  type Pagination = Record<string, any>;

  // i18n
  const { t } = useI18n();

  // route
  const router = useRouter();
  const route = useRoute();
  const id = (route.params.id as string) ?? '';

  // model
  const modelMetadata: { [key: string]: any } = {
    id: {
      type: 'int64',
      i18nKey: 'users--index.self-block.model.attributes.id',
    },
    name: {
      type: 'string',
      i18nKey: 'users--index.self-block.model.attributes.name',
    },
    username: {
      type: 'string',
      i18nKey: 'users--index.self-block.model.attributes.username',
    },
    avatar: {
      type: 'image',
      i18nKey: 'users--index.self-block.model.attributes.avatar',
    },
    email: {
      type: 'string',
      i18nKey: 'users--index.self-block.model.attributes.email',
    },
    phone: {
      type: 'string',
      i18nKey: 'users--index.self-block.model.attributes.phone',
    },
    website: {
      type: 'string',
      i18nKey: 'users--index.self-block.model.attributes.website',
    },
  };

  // search
  const searchMetadata: { [key: string]: any } = {
    id_eq: {
      name: 'id_eq',
      type: 'int64',
      i18nKey: 'users--index.self-block.searchers.id_eq',
    },
    name_cont: {
      name: 'name_cont',
      type: 'string',
      i18nKey: 'users--index.self-block.searchers.name_cont',
    },
  };
  const baseTableSearch: Search = {
    id_eq: null,
    name_cont: null,
  };
  const tableSearch = reactive({
    ...baseTableSearch,
  });
  const tableSearchForm = reactive({
    ...tableSearch,
  });
  const tableSearchCondition = reactive({
    ...tableSearch,
  });
  const tableSearchConditionCount = ref(0);
  const apiSearch = (search: Search): any => {
    const o = omitBy(search, (v, k) => {
      if (searchMetadata[k].optionals) return v == null || v === '';
      return v == null || v === '' || v === false;
    });
    Object.assign(tableSearchCondition, cloneDeep(baseTableSearch), cloneDeep(o));
    tableSearchConditionCount.value = Object.keys(o).length
    return isEmpty(o) ? null : o;
  };

  // sorter
  const baseTableSorter: Sorter = {
    name: '',
    direction: '',
  };
  const tableSorter = reactive({
    ...baseTableSorter,
  });
  const apiSorter = (sorter: Sorter): any => {
    if (sorter.name === '') return null;
    return { name: sorter.name, direction: sorter.direction };
  };

  // pagination
  const baseTablePagination: Pagination = {
    pageSize: 20,
    current: 1,
    total: 0,
    showTotal: true,
    hideOnSinglePage: true,
  };
  const tablePagination = reactive({
    ...baseTablePagination,
  });
  const apiPagination = (pagination: Pagination): any => {
    return {
      page_size: pagination.pageSize,
      current: pagination.current,
    };
  };

  // table - columns initialize
  const tableColumnsWithConfiguration = ref<Record<string, Column>>({
    id: {
      renderable: true,
    },
    name: {
      renderable: true,
    },
    username: {
      renderable: true,
    },
    avatar: {
      renderable: true,
    },
    email: {
      renderable: true,
    },
    phone: {
      renderable: true,
    },
    website: {
      renderable: true,
    },
    tableOperationsColumn: {
      renderable: true,
    },
  });
  const tableColumnsWithShow = ref<Column[]>([]);
  const tableColumnsShow = ref<Column[]>([]);
  const tableColumns = computed<Column[]>(() => {
    return ([
      {
        title: t('users--index.self-block.model.attributes.id'),
        dataIndex: 'id',
        slotName: 'id',
        sortable: {
          sortDirections: ['descend', 'ascend', ],
          sorter: true,
        },
        cellClass: 'column-id',
        hidden: false,
      },
      {
        title: t('users--index.self-block.model.attributes.name'),
        dataIndex: 'name',
        slotName: 'name',
        cellClass: 'column-name',
        hidden: false,
      },
      {
        title: t('users--index.self-block.model.attributes.username'),
        dataIndex: 'username',
        slotName: 'username',
        cellClass: 'column-username',
        hidden: false,
      },
      {
        title: t('users--index.self-block.model.attributes.avatar'),
        dataIndex: 'avatar',
        slotName: 'avatar',
        cellClass: 'column-avatar',
        hidden: false,
      },
      {
        title: t('users--index.self-block.model.attributes.email'),
        dataIndex: 'email',
        slotName: 'email',
        cellClass: 'column-email',
        hidden: false,
      },
      {
        title: t('users--index.self-block.model.attributes.phone'),
        dataIndex: 'phone',
        slotName: 'phone',
        cellClass: 'column-phone',
        hidden: false,
      },
      {
        title: t('users--index.self-block.model.attributes.website'),
        dataIndex: 'website',
        slotName: 'website',
        cellClass: 'column-website',
        hidden: false,
      },
      {
        title: t('table.columns.operations'),
        dataIndex: 'tableOperationsColumn',
        slotName: 'tableOperationsColumn',
        cellClass: 'column-operations',
      },
    ] as Column[]).filter((item) => {
      return tableColumnsWithConfiguration.value[item.dataIndex as string].renderable;
    });
  });
  watch(
    () => tableColumns.value,
    (val) => {
      tableColumnsWithShow.value = cloneDeep(val);
      tableColumnsWithShow.value.forEach((item) => {
        item.show = !item.hidden;
      });
      tableColumnsShow.value = tableColumnsWithShow.value.filter(
        (item) => item.show
      );
    },
    { deep: true, immediate: true }
  );

  // table - columns show/hide
  const handleTableColumnsShowChange = (
    checked: boolean | (string | boolean | number)[],
    column: Column,
    index: number
  ) => {
    tableColumnsShow.value = tableColumnsWithShow.value.filter(
      (item) => item.show
    );
  };

  // table - store
  const { loading, setLoading } = useLoading(true);
  const store = ref<Model[]>([]);
  const fetchStore = async (req: ListRequest) => {
    setLoading(true);
    try {
      const { data } = await list(id, req);
      const { collection, pagination } = data;

      if (collection) {
        store.value = collection;
      }

      if (pagination) {
        tablePagination.pageSize = pagination.page_size;
        tablePagination.current = pagination.current;
        tablePagination.total = pagination.total;
      } else {
        tablePagination.pageSize = baseTablePagination.pageSize;
        tablePagination.current = baseTablePagination.current;
        tablePagination.total = baseTablePagination.total;;
      }
    } finally {
      setLoading(false);
    }
  };
  const updateStore = (data: Record<string, any>) => {
    const { model } = data;
    if (model) {
      const foundIndex = store.value.findIndex((e) => String(e.id) === String(model.id));
      if (foundIndex !== -1) {
        store.value.splice(foundIndex, 1, model);
      }
    }
  };

  // table - data
  const tableData = computed(() => store.value);

  // table - refresh
  const onTableRefresh = async () => {
    const req = omitBy({
      search: apiSearch(tableSearch),
      sorter: apiSorter(tableSorter),
      pagination: apiPagination(tablePagination),
    }, v => v == null) as ListRequest;
    await fetchStore(req);
  };

  // table - pageChange
  const onTablePageChange = async (current: number) => {
    const req = omitBy({
      search: apiSearch(tableSearch),
      sorter: apiSorter(tableSorter),
      pagination: { ...apiPagination(tablePagination), current },
    }, v => v == null) as ListRequest;
    await fetchStore(req);
  };

  // table - search
  const onTableSearch = async () => {
    const req = omitBy({
      search: apiSearch(tableSearch),
      sorter: apiSorter(tableSorter),
      pagination: apiPagination(baseTablePagination),
    }, v => v == null) as ListRequest;
    await fetchStore(req);
  };

  // table - search condition
  const handleSearchConditionClose = async(name: string) => {
    if (name === '') {
      Object.assign(tableSearch, cloneDeep(baseTableSearch));
    } else {
      tableSearch[name] = null;
    }
    await onTableSearch();
  };

  // table - search modal
  const searchModalVisible = ref(false);
  const handleSearchModalVisible = () => {
    Object.assign(tableSearchForm, cloneDeep(tableSearch));
    searchModalVisible.value = true;
  };
  const handleSearchModalBeforeOk = async () => {
    Object.assign(tableSearch, cloneDeep(tableSearchForm));
    await onTableSearch();
    searchModalVisible.value = false;
  };
  const handleSearchModalCancel = () => {
    searchModalVisible.value = false;
  };

  // table - sorterChange
  const onTableSorterChange = async (dataIndex: string, direction: string) => {
    if (direction) {
      tableSorter.name = dataIndex;
      tableSorter.direction = direction;
    } else {
      tableSorter.name = baseTableSorter.name;
      tableSorter.direction = baseTableSorter.direction;
    }
    const req = omitBy({
      search: apiSearch(tableSearch),
      sorter: apiSorter(tableSorter),
      pagination: apiPagination(tablePagination),
    }, v => v == null) as ListRequest;
    await fetchStore(req);
  };

  // table - operations ui
  const tableOperationsColumnRenderableRef = ref();
  const tableOperationsColumnMoreRenderable = ref<boolean>(true);
  onMounted(() => {
    const el = tableOperationsColumnRenderableRef.value as any;
    if (el.children.length === 0) {
      tableColumnsWithConfiguration.value.tableOperationsColumn.renderable = false
    }
    if (el.querySelectorAll('.custom').length === 0) {
      tableOperationsColumnMoreRenderable.value = false
    }
  });

  // table - operations - goto
  const goto = (_route: Record<string, any>) => {
    router.push({ name: _route.name, params: _route.params, query: { back: route.path } });
  };

  // table - tabbable
  useTabbableViewBlock({
    viewName: 'UsersIndex',
    refreshFn: onTableRefresh,
  });

  // table - init
  onTableRefresh();
</script>
